import React, {Component} from "react";
import * as d3 from 'd3';

export default class Vis extends Component {
    // componentDidMount() {
    //     Network(this.props);
    // }
    // componentDidUpdate(prevProps) {
    //     Network(this.props);
    // }
    render() {
        const width = 1500;
        const height = 800;
        function color(d) {
            const scale = d3.scaleOrdinal(d3.schemeCategory10);
            return d => scale(d.group);
        }
       function drag(simulation) {
            function dragStart(d) {
                if (!d3.event.active) {
                    simulation.alphaTarget(0.3).restart();
                }
                    d.fx = d.x;
                    d.fy = d.y;
            }
            function dragged(d) {
                d.fx = d3.event.x;
                d.fy = d3.event.y;
            }
            function dragEnd(d) {
                if (!d3.event.active) {
                    simulation.alphaTarget(0);
                }
                d.fx = null;
                d.fy = null;
            }
            return d3.drag()
                .on("start", dragStart)
                .on("drag", dragged)
                .on("end", dragEnd);
        }
        d3.csv("./all-nodes.csv").then(data => {
            console.log(data);
            let firstLayer = data.filter(obj => +obj["parent"] === 0);
            console.log(firstLayer);
            let defaultNet = {
                node:[],
                link:[]
            };
            firstLayer.forEach(obj => {
                defaultNet.node.push({id: obj.name, group: +obj.numChildren});
                if (obj.name !== "root") {
                    defaultNet.link.push({source: "root", target: obj.name, value: +obj.numChildren});
                }
            });
            console.log(defaultNet);
            const defaultLinks = defaultNet.link.map(d => Object.create(d));
            const defaultNodes = defaultNet.node.map(d => Object.create(d));
            const simulation = d3.forceSimulation(defaultNodes)
                .force("link", d3.forceLink(defaultLinks).id(d => d.id))
                .force("charge", d3.forceManyBody())
                .force("center", d3.forceCenter(width / 2, height / 2));
            const svg = d3.select("svg")
                .attr("viewBox", [0, 0, width, height]);
            const link = svg.append("g")
                .attr("stroke", "#999")
                .attr("stroke-opacity", 0.6)
                .selectAll("line")
                .data(defaultLinks)
                .join("line")
                .attr("stroke-width", d => Math.sqrt(d.value));
            const node = svg.append("g")
                .attr("stroke", "#fff")
                .attr("stroke-width", 1.5)
                .selectAll("circle")
                .data(defaultNodes)
                .join("circle")
                .attr("r", 5)
                .attr("fill", color)
                .call(drag(simulation));
            node.append("title")
                .text(d => d.id);
            simulation.on("tick", () => {
                link
                    .attr("x1", d => d.source.x)
                    .attr("y1", d => d.source.y)
                    .attr("x2", d => d.target.x)
                    .attr("y2", d => d.target.y);
                node
                    .attr("cx", d => d.x)
                    .attr("cy", d => d.y);
            });
        });
        return (
            <svg className="vis" width={width} height={height}/>
        )
    }
}
