import React, {Component} from "react";
import * as d3 from 'd3';

export default class Vis extends Component {
    // componentDidMount() {
    //     Network(this.props);
    // }
    // componentDidUpdate(prevProps) {
    //     Network(this.props);
    // }
    render() {
        const width = 1500;
        const height = 800;
        d3.csv("./all-nodes.csv").then(data => {
            console.log(data);
            d3.select("svg")
                .append("circle")
                .attr("r", 5)
                .attr("cx", width / 2)
                .attr("cy", height / 2)
                .attr("fill", "red");
        });
        return (
            <svg className="vis" width={width} height={height}/>
        )
    }
}
